mapper.sql:
where DEL_FLAG = 0



                and l.WORD_AUTHOR_ID = #{page.data.wordAuthorId}


                and l.CITY_CODE = #{page.data.cityCode}

mapper.select:
select
from LEAVE_MESSAGE l
order by l.CREATE_DATE desc limit #{page.startIndex},#{page.pageSize}

select l.ID,l.CITY_CODE,l.PARENT_ID,l.WORD_AUTHOR_ID,l.WORD_MASTER_ID,l.MSG_CONTENT,l.CREATE_DATE,l.AREA_CODE,b.BASE_NAME from LEAVE_MESSAGE l left join BASE_DETAIL b on l.AREA_CODE = b.ID WHERE 1=1 AND l.DEL_FLAG = 0 and l.PARENT_ID is null
AND l.AREA_CODE = #{page.data.cityCode}
ORDER BY l.CREATE_DATE DESC LIMIT #{page.pageNum},#{page.pageSize}
